import React from 'react';
import { Route, Switch, Redirect, withRouter, useHistory } from 'react-router-dom';
import Header from './Header';
import success from '../images/success.svg';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from './ImagePopup';
import api from '../utils/api';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import Login from './Login';
import {CurrentUserContext} from '../contexts/CurrentUserContext';
import Register from './Register';
import ProtectedRoute from './ProtectedRoute';
import InfoTooltip from './InfoTooltip';
import * as auth from '../auth.js';
import '../index.css';

function App() {
    const history = useHistory();
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState({})
    const [currentUser, setCurrentUser] = React.useState({});
    const [cards, setCards] = React.useState([]);
    const [loggedIn, setLoggedIn] = React.useState(false);

    function handleLogin(e) {
        e.preventDefault();
        setLoggedIn({
            loggedIn: true
        })
    }

    function tokenCheck () {
        // если у пользователя есть токен в localStorage, 
        // эта функция проверит, действующий он или нет
        if (localStorage.getItem('jwt')){
          const jwt = localStorage.getItem('jwt');
          if (jwt){
            // проверим токен
            auth.getContent(jwt).then((res) => {
              if (res){
                        // авторизуем пользователя
                setLoggedIn({
                  loggedIn: true,
                }), 
                            // обернём App.js в withRouter
                            // так, что теперь есть доступ к этому методу
                history.push("/")

              }
            }); 
          }

        }
    }

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);
        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }
    function handleCardDelete(card) {
        api.removeCard(card._id)
        .then((data) => {
            setCards((cards) => cards.filter(item => item._id !== card._id))
        })
    }
    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true)
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true)
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true)
    }

    function handleCardClick(card) {
        setSelectedCard(card)
    }
    function closeAllPopups () {
        setIsEditProfilePopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setIsEditAvatarPopupOpen(false)
        setSelectedCard({})
    } 
    function handleUpdateUser(userInfo) {
        api.editProfileInfo(userInfo)
        .then((info) => {
            setCurrentUser(info);
            closeAllPopups();
        })
        .catch(err => console.log(err));
    }
    function handleUpdateAvatar(ava) {
        api.updateAvatar(ava.avatar)
        .then((data) => {
            setCurrentUser(data);
            closeAllPopups();
        })
        .catch(err => console.log(err));
    }
    function handleAddPlace(item) {
        api.createCard(item)
        .then((newCard) => {
            setCards([newCard, ...cards])
            closeAllPopups()
        })
        .catch(err => console.log(err));
    }
    React.useEffect(tokenCheck(), [])
    React.useEffect(() => {
        api.getProfileInfo()
        .then((data) => {
            setCurrentUser(data)
        })
        .catch(err => console.log(err));
        api.getInitialCards()
        .then((data) => {
            setCards(data)
        })
        .catch(err => console.log(err));
    }, [])
  return (
          <CurrentUserContext.Provider value={currentUser}>
                <div className="page">
                <main className="cont">
                        <Switch>
                            <Route path="/sign-up">
                                <Register />
                            </Route>
                            <Route path="/sign-in">
                                <Login handleLogin = {handleLogin} />
                            </Route>
                            <ProtectedRoute
                                path="/"
                                loggedIn={loggedIn}
                                component={Main}
                                cards = {cards} 
                                onCardLike = {handleCardLike} 
                                onCardDelete = {handleCardDelete} 
                                onEditProfile = {handleEditProfileClick} 
                                onAddPlace = {handleAddPlaceClick} 
                                onEditAvatar = {handleEditAvatarClick} 
                                onCardClick = {handleCardClick} 
                            />
                        </Switch>
                    <Footer />
                    <EditProfilePopup 
                        isOpen={isEditProfilePopupOpen} 
                        onClose={closeAllPopups} 
                        onUpdateUser = {handleUpdateUser} 
                    /> 
                    <AddPlacePopup 
                        isOpen={isAddPlacePopupOpen} 
                        onClose={closeAllPopups} 
                        onAddPlace = {handleAddPlace} 
                    />
                    <EditAvatarPopup 
                        isOpen={isEditAvatarPopupOpen} 
                        onClose={closeAllPopups} 
                        onUpdateAvatar = {handleUpdateAvatar} 
                    />
                    <ImagePopup card = {selectedCard} onClose = {closeAllPopups} />
                </main>
            </div>
        </CurrentUserContext.Provider>

  );
}

export default App;
