import React from 'react'
import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import api from '../utils/api';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import {CurrentUserContext} from '../contexts/CurrentUserContext';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setIsSelectedCard] = React.useState([])
    const [currentUser, setCurrentUser] = React.useState({});
    const [cards, setCards] = React.useState([]);
    React.useEffect(() => {
        api.getInitialCards()
        .then((data) => {
            setCards(data)
        })
        .catch(err => console.log(err));
    }, [])
    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }
    function handleCardDelete(card) {
        api.removeCard(card._id)
        .then((data) => {
            cards.filter(card => card._id !== data._id)
        })
    }
    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true)
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true)
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true)
    }

    function handleCardClick(card) {
        setIsSelectedCard(card)
    }
    function closeAllPopups () {
        setIsEditProfilePopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setIsEditAvatarPopupOpen(false)
        setIsSelectedCard(false)
    } 
    function handleUpdateUser(prof) {
        api.editProfileInfo(prof)
        .then((info) => {
            setCurrentUser(info);
            closeAllPopups();
        })
        .catch(err => console.log(err));
    }
    function handleUpdateAvatar(ava) {
        api.updateAvatar(ava.avatar)
        .then((data) => {
            setCurrentUser(data);
            closeAllPopups();
        })
        .catch(err => console.log(err));
    }
    React.useEffect(() => {
        api.getProfileInfo()
        .then((data) => {
            setCurrentUser(data)
        })
        .catch(err => console.log(err));
    }, [])
  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className="page">
        <Header />
        <Main cards = {cards} onCardLike = {handleCardLike} onCardDelete = {handleCardDelete} onEditProfile = {handleEditProfileClick} onAddPlace = {handleAddPlaceClick} onEditAvatar = {handleEditAvatarClick} onCardClick = {handleCardClick} />
        <Footer />
        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser = {handleUpdateUser} /> 
        <PopupWithForm isOpen = {isAddPlacePopupOpen} name ="add" title = "Новое место" onClose = {closeAllPopups}>
            <label className="popup__form-field">
                <input id="place-title-input" type="text" placeholder="Название" name="name" className="popup__text popup__text_type_title" required minLength="2" maxLength="30" />
                <span className="place-title-input-error popup__text-error"></span>
            </label>
            <label className="popup__form-field">
                <input id="place-link-input" type="url" placeholder="Ссылка на картинку" name="link" className="popup__text popup__text_type_link" required />
                <span className="place-link-input-error popup__text-error"></span>
            </label>
            <button type="submit" value="Создать" name="save" className="popup__submit-button">Создать</button>
        </PopupWithForm>
        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar = {handleUpdateAvatar} />
        <ImagePopup card = {selectedCard} onClose = {closeAllPopups} />
        </div>
        </CurrentUserContext.Provider>
  );
}

export default App;
