import '../index.css';
import React from 'react';
import { useHistory, useEffect } from 'react-router-dom';
import Header from './Header';
import * as Auth from '../auth';

function Register() {
    const history = useHistory();
    const [inputs, setInputs] = React.useState({
        email: '',
        password: '',
    });
    function handleChange(e) {
        const {name, value} = e.target;
        setInputs({
          [name]: value 
        });
    }
    function handleSubmit(e){
        e.preventDefault();
        let { email, password } = inputs;
        Auth.register(email, password).then((res) => {
            if(res){
                setInputs({
                  message: ''
                }, () => {
                  useEffect(history.push('/sign-in'));
                })
              } else {
                setInputs({
                  message: 'Что-то пошло не так!'
                })
              }
        })
      // сюда добавим логику обработки формы регистрации
    }
    return (
        <section className="login">
            <Header path='/sign-in' text="Войти" />
            <div className="login__container">
                <h3 className="login__title">Регистрация</h3>
                <form className="login__form" name = "login" onSubmit={handleSubmit}>
                    <label className="popup__form-field">
                        <input id="email-input" type="email" placeholder="Email" name="email" className="login__text" onChange={handleChange} required minLength="2" maxLength="200" />
                        <span className="email-input-error popup__text-error"></span>
                    </label>
                    <label className="popup__form-field">
                        <input id="password-input" type="password" placeholder="Пароль" name="password" className="login__text" onChange={handleChange} required minLength="2" maxLength="200" />
                        <span className="email-input-error popup__text-error"></span>
                    </label>
                    <button type="submit" value="Зарегистрироваться" name="login" className="login__submit-button">Зарегистрироваться</button>
                    <p className="login__txtarea">Уже зарегистрированы? <a className="login__a" href="/sign-in">Войти</a></p>
                </form>
            </div>
        </section>
    );
  }

  export default Register;
