import React from 'react';
import api from '../utils/api';
import Card from './Card';
import editIcon from '../images/edit-icon.svg'
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Main(props) {
    const currentUser = React.useContext(CurrentUserContext)
    const [cards, setCards] = React.useState([]);
    React.useEffect(() => {
        api.getInitialCards()
        .then((data) => {
            setCards(data)
        })
        .catch(err => console.log(err));
    }, [])
    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }
    function handleCardDelete(card) {
        api.removeCard(card._id)
        .then((data) => {
            cards.filter(card => card._id !== data._id)
        })
    }
    return (
        <main className="content">
                <section className="profile">
                    <div className="profile__info">
                        <div className="profile__avatar-block">
                            <div className="avatar" style={{ backgroundImage: `url(${currentUser.avatar})` }}></div>
                            <div className="profile__wrap-edit" onClick = {props.onEditAvatar}>
                                <img src={editIcon} alt="Редактирование" className="profile__icon" />
                            </div>
                        </div>
                        <div>
                            <div className="profile__text-info">
                                <h1 className="profile__name">{currentUser.name}</h1>
                                <button className="profile__edit-button" type="button" onClick = {props.onEditProfile}></button>
                            </div>
                            <p className="profile__about">{currentUser.about}</p>
                        </div>
                    </div>
                    <button className="profile__add-button" type="button" onClick = {props.onAddPlace}></button>
                </section>
                <section className="elements">
                {cards.map((card) => {
                        return (
                            <Card card = {card} onCardClick = {props.onCardClick} key={card._id} onCardLike = {handleCardLike} onCardDelete = {handleCardDelete} />
                        )
                    })}
                </section>
            </main>
    );
  }

  export default Main;
