import '../index.css';
import Header from './Header';

function Login() {
    return (
        <section className="login">
            <Header path='/sign-up' text="Регистрация" />
            <div className="login__container">
                <h3 className="login__title">Вход</h3>
                <form className="login__form" name = "login" onSubmit="">
                    <label className="popup__form-field">
                        <input id="email-input" type="email" placeholder="Email" name="email" className="login__text" required minLength="2" maxLength="200" />
                        <span className="email-input-error popup__text-error"></span>
                    </label>
                    <label className="popup__form-field">
                        <input id="password-input" type="password" placeholder="Пароль" name="password" className="login__text" required minLength="2" maxLength="200" />
                        <span className="email-input-error popup__text-error"></span>
                    </label>
                    <button type="submit" value="Войти" name="login" className="login__submit-button">Войти</button>
                </form>
            </div>
        </section>
    );
  }

  export default Login;
